mapper.insert:
INSERT INTO TBL_USER (
	USER_ID, PASSWORD, USER_NM, WDATE, USE_YN, EMAIL
) VALUES (
	#{userId}, #{password}, #{userNm}, NOW(), 'Y', #{email}
)

mapper.select:
SELECT USER_ID
  FROM TBL_USER
 WHERE USER_ID = #{userId}

SELECT USER_ID, USER_NM
  FROM TBL_USER
 WHERE USER_ID = #{userId}
   AND PASSWORD = #{password}
