mapper.insert:
INSERT INTO TBL_USER (
	USERID, PASSWORD, USERNM, WDATE, USE_YN, EMAIL
) VALUES (
	#{userId}, #{password}, #{userNm}, NOW(), 'Y', #{email}
)

mapper.select:
SELECT USERID
  FROM TBL_USER
 WHERE USERID = #{userId}

SELECT USERID, USERNM
  FROM TBL_USER
 WHERE USERID = #{userId}
   AND PASSWORD = #{password}
